import React from 'react';
import styled from 'styled-components';
import { ReactComponent as LokanexIcon } from '../assets/Lokanexlogo.svg';

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.md};
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  padding: 8px;
  border-radius: 12px;

  &:hover {
    transform: scale(1.05);
    background: rgba(0, 212, 255, 0.05);
  }
`;

const LogoIcon = styled.div`
  width: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  height: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const LogoText = styled.div`
  display: flex;
  align-items: center;
  font-family: ${props => props.theme.fonts.logo};
  font-weight: ${props => props.theme.fontWeights.extrabold};
  font-size: ${props => props.size === 'large' ? props.theme.fontSizes['3xl'] : 
                        props.size === 'medium' ? props.theme.fontSizes['2xl'] : 
                        props.theme.fontSizes.xl};
  letter-spacing: 0.05em;
  text-transform: capitalize;
  position: relative;
`;

const LogoPart = styled.span`
  background: ${props => props.isFirst ? 
    'linear-gradient(135deg, #00d4ff 0%, #0099cc 100%)' : 
    'linear-gradient(135deg, #ff6b35 0%, #ff8c42 100%)'};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 0;
    width: 0;
    height: 2px;
    background: ${props => props.isFirst ? 
      'linear-gradient(90deg, #00d4ff, #ff6b35)' : 
      'linear-gradient(90deg, #ff6b35, #00d4ff)'};
    transition: width 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  ${LogoContainer}:hover &::after {
    width: 100%;
  }

  ${LogoContainer}:hover & {
    transform: translateY(-1px);
    filter: brightness(1.1);
  }
`;

const LokanexLogo = ({ size = 'medium', showText = true, ...props }) => {
  return (
    <LogoContainer {...props}>
      <LogoIcon size={size}>
        <LokanexIcon 
          width="100%" 
          height="100%"
        />
      </LogoIcon>
      {showText && (
        <LogoText size={size}>
          <LogoPart isFirst={true}>Lo</LogoPart>
          <LogoPart isFirst={false}>kanex</LogoPart>
        </LogoText>
      )}
    </LogoContainer>
  );
};

export default LokanexLogo;
