import React from 'react';
import styled from 'styled-components';
import { ReactComponent as LokanexIcon } from '../assets/Lokanexlogo.svg';

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.md};
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  padding: 8px;
  border-radius: 12px;

  &:hover {
    transform: scale(1.05);
    background: rgba(0, 212, 255, 0.05);
  }
`;

const LogoIcon = styled.div`
  width: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  height: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  filter: drop-shadow(0 0 8px rgba(255, 107, 107, 0.3));
  animation: rainbowGlow 3s ease-in-out infinite;

  @keyframes rainbowGlow {
    0%, 100% {
      filter: drop-shadow(0 0 8px rgba(255, 107, 107, 0.3));
    }
    16% {
      filter: drop-shadow(0 0 8px rgba(78, 205, 196, 0.3));
    }
    32% {
      filter: drop-shadow(0 0 8px rgba(69, 183, 209, 0.3));
    }
    48% {
      filter: drop-shadow(0 0 8px rgba(150, 206, 180, 0.3));
    }
    64% {
      filter: drop-shadow(0 0 8px rgba(254, 202, 87, 0.3));
    }
    80% {
      filter: drop-shadow(0 0 8px rgba(255, 159, 243, 0.3));
    }
  }
`;

const LogoText = styled.span`
  font-family: ${props => props.theme.fonts.logo};
  font-weight: ${props => props.theme.fontWeights.extrabold};
  font-size: ${props => props.size === 'large' ? props.theme.fontSizes['3xl'] : 
                        props.size === 'medium' ? props.theme.fontSizes['2xl'] : 
                        props.theme.fontSizes.xl};
  background: linear-gradient(
    45deg,
    #ff6b6b,
    #4ecdc4,
    #45b7d1,
    #96ceb4,
    #feca57,
    #ff9ff3,
    #54a0ff,
    #5f27cd,
    #00d2d3,
    #ff9f43,
    #ff6b6b
  );
  background-size: 400% 400%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: 0.05em;
  text-transform: capitalize;
  position: relative;
  animation: rainbowFlow 4s ease-in-out infinite;

  &::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 0;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #feca57, #ff9ff3);
    background-size: 200% 200%;
    animation: rainbowFlow 2s ease-in-out infinite;
    transition: width 0.3s ease;
  }

  ${LogoContainer}:hover &::after {
    width: 100%;
  }

  @keyframes rainbowFlow {
    0% {
      background-position: 0% 50%;
    }
    25% {
      background-position: 100% 50%;
    }
    50% {
      background-position: 100% 100%;
    }
    75% {
      background-position: 0% 100%;
    }
    100% {
      background-position: 0% 50%;
    }
  }
`;

const LokanexLogo = ({ size = 'medium', showText = true, ...props }) => {
  return (
    <LogoContainer {...props}>
      <LogoIcon size={size}>
        <LokanexIcon 
          width="100%" 
          height="100%"
        />
      </LogoIcon>
      {showText && <LogoText size={size}>Lokanex</LogoText>}
    </LogoContainer>
  );
};

export default LokanexLogo;
