import React from 'react';

const ArrowIcon = ({ size = 24, color = "#00bfff", ...props }) => {
  return (
    <svg
      width={size}
      height={size}
      viewBox="0 0 100 100"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      {...props}
    >
      {/* Glow effect */}
      <filter id="glow">
        <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
        <feMerge> 
          <feMergeNode in="coloredBlur"/>
          <feMergeNode in="SourceGraphic"/>
        </feMerge>
      </filter>

      {/* Shadow */}
      <path
        d="M25 65 L25 45 L45 45 L45 25 L75 50 L45 75 L45 55 L25 55 Z"
        fill="rgba(0, 0, 0, 0.2)"
        transform="translate(2, 2)"
      />

      {/* Main arrow shape */}
      <path
        d="M25 60 L25 40 L40 40 L40 25 L70 50 L40 75 L40 50 L25 50 Z"
        fill={color}
        filter="url(#glow)"
      />
    </svg>
  );
};

export default ArrowIcon;
