import React from 'react';
import styled from 'styled-components';

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  cursor: pointer;
  transition: ${props => props.theme.transitions.base};

  &:hover {
    transform: scale(1.05);
  }
`;

const LogoIcon = styled.div`
  width: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  height: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const LogoText = styled.span`
  font-family: ${props => props.theme.fonts.primary};
  font-weight: ${props => props.theme.fontWeights.bold};
  font-size: ${props => props.size === 'large' ? props.theme.fontSizes['2xl'] : 
                        props.size === 'medium' ? props.theme.fontSizes.xl : 
                        props.theme.fontSizes.lg};
  background: ${props => props.theme.colors.gradientPrimary};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: -0.02em;
`;

const LokanexLogo = ({ size = 'medium', showText = true, ...props }) => {
  return (
    <LogoContainer {...props}>
      <LogoIcon size={size}>
        <svg
          width="100%"
          height="100%"
          viewBox="0 0 100 100"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          {/* Background glow */}
          <circle
            cx="50"
            cy="50"
            r="48"
            fill="url(#bgGlow)"
            opacity="0.3"
          />

          {/* Main L shape with modern twist */}
          <path
            d="M25 25 L25 75 L65 75 L65 55 L45 55 L45 25 Z"
            fill="url(#mainGradient)"
            stroke="url(#strokeGradient)"
            strokeWidth="0.5"
          />

          {/* Floating geometric elements */}
          <circle cx="75" cy="30" r="3" fill="url(#accentGradient)" />
          <rect x="70" y="65" width="6" height="6" fill="url(#accentGradient2)" rx="1" />
          <polygon points="20,20 25,20 22.5,25" fill="url(#accentGradient3)" />

          {/* Connecting lines */}
          <line x1="45" y1="55" x2="75" y2="30" stroke="url(#lineGradient)" strokeWidth="1.5" opacity="0.6" />
          <line x1="25" y1="25" x2="22.5" y2="25" stroke="url(#lineGradient2)" strokeWidth="1" opacity="0.5" />
          <line x1="65" y1="75" x2="73" y2="68" stroke="url(#lineGradient3)" strokeWidth="1" opacity="0.5" />

          {/* Central highlight */}
          <circle cx="50" cy="50" r="8" fill="url(#highlightGradient)" opacity="0.4" />

          {/* Outer ring with tech pattern */}
          <circle
            cx="50"
            cy="50"
            r="42"
            stroke="url(#outerRing)"
            strokeWidth="1.5"
            fill="none"
            strokeDasharray="2,2"
            opacity="0.7"
          />

          {/* Gradients */}
          <defs>
            <radialGradient id="bgGlow" cx="50%" cy="50%" r="50%">
              <stop offset="0%" stopColor="#00d4ff" stopOpacity="0.4" />
              <stop offset="70%" stopColor="#4ecdc4" stopOpacity="0.2" />
              <stop offset="100%" stopColor="#ff6b35" stopOpacity="0.1" />
            </radialGradient>
            <linearGradient id="mainGradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#00d4ff" />
              <stop offset="30%" stopColor="#4ecdc4" />
              <stop offset="70%" stopColor="#0099cc" />
              <stop offset="100%" stopColor="#00d4ff" />
            </linearGradient>
            <linearGradient id="strokeGradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#ffffff" />
              <stop offset="100%" stopColor="#e0e0e0" />
            </linearGradient>
            <linearGradient id="accentGradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#ff6b35" />
              <stop offset="100%" stopColor="#ff8c42" />
            </linearGradient>
            <linearGradient id="accentGradient2" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#4ecdc4" />
              <stop offset="100%" stopColor="#44a08d" />
            </linearGradient>
            <linearGradient id="accentGradient3" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#00d4ff" />
              <stop offset="100%" stopColor="#0099cc" />
            </linearGradient>
            <linearGradient id="lineGradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#00d4ff" />
              <stop offset="100%" stopColor="#ff6b35" />
            </linearGradient>
            <linearGradient id="lineGradient2" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#00d4ff" />
              <stop offset="100%" stopColor="#4ecdc4" />
            </linearGradient>
            <linearGradient id="lineGradient3" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#4ecdc4" />
              <stop offset="100%" stopColor="#44a08d" />
            </linearGradient>
            <radialGradient id="highlightGradient" cx="50%" cy="50%" r="50%">
              <stop offset="0%" stopColor="#ffffff" stopOpacity="0.6" />
              <stop offset="100%" stopColor="#00d4ff" stopOpacity="0.2" />
            </radialGradient>
            <linearGradient id="outerRing" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#00d4ff" />
              <stop offset="50%" stopColor="#4ecdc4" />
              <stop offset="100%" stopColor="#ff6b35" />
            </linearGradient>
          </defs>
        </svg>
      </LogoIcon>
      {showText && <LogoText size={size}>Lokanex</LogoText>}
    </LogoContainer>
  );
};

export default LokanexLogo;
