import React from 'react';
import styled from 'styled-components';
import { ReactComponent as LokanexIcon } from '../assets/Lokanexlogo.svg';

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.md};
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  position: relative;
  padding: 12px;
  border-radius: 20px;

  &:hover {
    transform: scale(1.08) rotate(1deg);

    &::before {
      opacity: 1;
      transform: scale(1.1);
    }

    ${LogoIcon} {
      transform: rotate(360deg) scale(1.1);
    }

    ${LogoText} {
      animation-play-state: paused;
      filter: brightness(1.2);
    }
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, 
      rgba(0, 212, 255, 0.1), 
      rgba(255, 107, 53, 0.1), 
      rgba(78, 205, 196, 0.1),
      rgba(0, 255, 136, 0.1)
    );
    border-radius: 20px;
    opacity: 0;
    transition: all 0.3s ease;
    z-index: -1;
  }
`;

const LogoIcon = styled.div`
  width: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  height: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const LogoText = styled.span`
  font-family: ${props => props.theme.fonts.logo};
  font-weight: ${props => props.theme.fontWeights.extrabold};
  font-size: ${props => props.size === 'large' ? props.theme.fontSizes['3xl'] : 
                        props.size === 'medium' ? props.theme.fontSizes['2xl'] : 
                        props.theme.fontSizes.xl};
  background: ${props => props.theme.colors.gradientPrimary};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: 0.05em;
  text-transform: capitalize;
  text-shadow: 0 0 30px rgba(0, 212, 255, 0.3);
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, #00d4ff, #ff6b35, #4ecdc4, #00d4ff);
    background-size: 300% 300%;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: gradientShift 3s ease-in-out infinite;
    opacity: 0.7;
  }

  @keyframes gradientShift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
  }
`;

const LokanexLogo = ({ size = 'medium', showText = true, ...props }) => {
  return (
    <LogoContainer {...props}>
      <LogoIcon size={size}>
        <LokanexIcon 
          width="100%" 
          height="100%"
        />
      </LogoIcon>
      {showText && <LogoText size={size}>Lokanex</LogoText>}
    </LogoContainer>
  );
};

export default LokanexLogo;
