import React from 'react';
import styled from 'styled-components';
import { ReactComponent as LokanexIcon } from '../assets/Lokanexlogo.svg';

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  position: relative;
  padding: 8px;
  border-radius: 12px;

  &:hover {
    transform: scale(1.08) rotate(1deg);

    &::before {
      opacity: 1;
      transform: scale(1.1);
    }
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, rgba(0, 212, 255, 0.1), rgba(255, 107, 53, 0.1), rgba(78, 205, 196, 0.1));
    border-radius: 12px;
    opacity: 0;
    transition: all 0.3s ease;
    z-index: -1;
  }
`;

const LogoIcon = styled.div`
  width: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  height: ${props => props.size === 'large' ? '48px' : props.size === 'medium' ? '36px' : '28px'};
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const LogoText = styled.span`
  font-family: ${props => props.theme.fonts.logo};
  font-weight: ${props => props.theme.fontWeights.extrabold};
  font-size: ${props => props.size === 'large' ? props.theme.fontSizes['3xl'] : 
                        props.size === 'medium' ? props.theme.fontSizes['2xl'] : 
                        props.theme.fontSizes.xl};
  background: linear-gradient(135deg, #00d4ff 0%, #ff6b35 25%, #4ecdc4 50%, #ff6b35 75%, #00d4ff 100%);
  background-size: 400% 400%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: 0.1em;
  text-transform: capitalize;
  position: relative;
  animation: gradientFlow 4s ease-in-out infinite, textGlow 2s ease-in-out infinite alternate;

  &::before {
    content: 'Lokanex';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, #ff006e, #8338ec, #3a86ff, #06ffa5);
    background-size: 300% 300%;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: gradientShift 3s ease-in-out infinite;
    opacity: 0.8;
    filter: blur(1px);
  }

  &::after {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: linear-gradient(45deg, #00d4ff, #ff6b35, #4ecdc4, #00d4ff);
    background-size: 300% 300%;
    border-radius: 8px;
    z-index: -1;
    opacity: 0.3;
    animation: gradientShift 3s ease-in-out infinite;
    filter: blur(8px);
  }

  @keyframes gradientFlow {
    0%, 100% { 
      background-position: 0% 50%;
      transform: scale(1);
    }
    25% { 
      background-position: 100% 50%;
      transform: scale(1.02);
    }
    50% { 
      background-position: 50% 100%;
      transform: scale(1);
    }
    75% { 
      background-position: 50% 0%;
      transform: scale(1.02);
    }
  }

  @keyframes gradientShift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
  }

  @keyframes textGlow {
    0% { 
      text-shadow: 0 0 20px rgba(0, 212, 255, 0.5),
                   0 0 40px rgba(255, 107, 53, 0.3),
                   0 0 60px rgba(78, 205, 196, 0.2);
    }
    100% { 
      text-shadow: 0 0 30px rgba(0, 212, 255, 0.8),
                   0 0 60px rgba(255, 107, 53, 0.5),
                   0 0 90px rgba(78, 205, 196, 0.3);
    }
  }
`;

const LokanexLogo = ({ size = 'medium', showText = true, ...props }) => {
  return (
    <LogoContainer {...props}>
      <LogoIcon size={size}>
        <LokanexIcon 
          width="100%" 
          height="100%"
        />
      </LogoIcon>
      {showText && <LogoText size={size}>Lokanex</LogoText>}
    </LogoContainer>
  );
};

export default LokanexLogo;
